<?phpclass C_User extends C_Base{    //    // Конструктор.    //    public function action_auth()    {        $this->title .= '::Авторизация';        if ($_POST) {            $login = htmlspecialchars(trim($_POST['login']));            $pass = htmlspecialchars(trim($_POST['pass']));            $user = new M_User();            if ($user->auth($login, $pass)) {                $info = 'Привет, ' . $login . '!' . '<br>' . ' Это твой личный кабинет.';                $this->showAuthForm = false;                $this->showExitButton = true;            } else {                $info = "Ошибка авторизации, ты зарегистрировался на сайте?";                $this->userAction = 'reg';                $this->buttonCaption = 'Регистрация';            }        }        $this->content = $this->Template(__DIR__ . '/../v/v_auth.php', ['text' => $info]);    }    public function action_reg()    {        $this->title .= '::Регистрация';        $info = "Тебя нет в базе, скорее регистрируйся!";        if ($_POST) {            $login = htmlspecialchars(trim($_POST['login']));            $pass = htmlspecialchars(trim($_POST['pass']));            $user = new M_User();            if ($user->reg($login, $pass)) {                $info = 'Привет, ' . $login . '!' . '<br>' . ' Ты успешно зарегистрировался, заходи на сайт.';                $this->showAuthForm = false;                $this->showExitButton = true;                $this->userAction = 'auth';                $this->buttonCaption = 'Войти';            }        }  else {            $info = "Ошибка регистрации, попробуй еще раз!";            $this->userAction = 'reg';            $this->buttonCaption = 'Регистрация';        }        $this->content = $this->Template(__DIR__ . '/../v/v_auth.php', ['text' => $info]);    }}